import React, { useRef } from 'react'
import {Form, Button} from 'react-bootstrap';
import axios from '../axios';
import { useNavigate } from 'react-router-dom'

const Login = () => {
    
    const idRef = useRef();
    const pwRef = useRef();

    const navigate = useNavigate();

  const handleLogin = (e)=>{
    e.preventDefault();
    axios.post('/user/login', {id : idRef.current.value, pw : pwRef.current.value})
    .then((res)=>{
      if(res.data.result === 'success'){
        alert('로그인 성공')
        navigate('/main')
      }else{
        alert('아이디 혹은 비밀번호를 확인해주세요.')
        navigate('/login')
      }
    })
  }

  return (
    <div>
        <h1>로그인</h1>
      <Form onSubmit={handleLogin}>
        <Form.Group className="mb-3" controlId="formBasicID">
          <Form.Label>ID</Form.Label>
          <Form.Control type="id" placeholder="Enter id" ref={idRef}/>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" ref={pwRef}/>
        </Form.Group>


        <div className='d-grid gap mb-3'>
          <Button variant="info" type="submit">
            Submit
          </Button>
        </div>
      </Form>
  </div>
  )
}

export default Login
